import React, { useState }  from 'react'
import TextField from '@mui/material/TextField'
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';

const ExpenseTrackerForm = ({Balance, setBalance}) => {

    const [Type, setType] = useState('')
    const [Note, setNote] = useState('')
    const [Amount, setAmount] = useState()


    const handleChange = (event) => {
        setType(event.target.value);
    }
    return (
        <div>
             <form >
                    <TextField
                        sx={{ m: 1, minWidth: 240 }}
                            label="Total Amount"
                            id="standard-size-normal"
                            defaultValue={Balance}
                            variant="standard"
                            className='row text-left'
                            name='balance'
                            onChange={(e)=>setBalance(e.target.value)}
                        />

                        <FormControl variant="standard" sx={{ m: 1, minWidth: 240 }} className='row text-left'>
                            <InputLabel>Transaction Type</InputLabel>
                            <Select
                                id="Transaction_type"
                                value={Type}
                                onChange={handleChange}
                                label="Transaction Type" 
                                name='transaction_type'
                            >
                                <MenuItem value='+'>Credit</MenuItem>
                                <MenuItem value='-'>Debit</MenuItem>
                            </Select>
                        </FormControl>

                        <TextField
                        sx={{ m: 1, minWidth: 240 }}
                            label="Amount Credited/Debited"
                            id="standard-size-normal"
                            variant="standard"
                            className='row text-left'
                            name='amount'
                            onChange={(e)=>setNote(e.target.value)}
                        />
                        <TextField
                        sx={{ m: 1, minWidth: 240 }}
                            label="Add Note"
                            id="standard-size-normal"
                            variant="standard"
                            className='row text-left'
                            name='note'
                            onChange={(e)=>setNote(e.target.value)}
                        />
                   
                    </form>
            
        </div>
    )
}

export default ExpenseTrackerForm
