import React, { useState } from 'react'
import InputFields from '../components/InputFields'
import Expensetracker from '../assets/expenseTracker.PNG'
import TextField from '@mui/material/TextField'
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';

const ExpenseTracker = () => {
    const [Type, setType] = useState('')
    const [TotalAmount, setTotalAmount] = useState()
    const [Balance, setBalance] = useState(0.00)

    const handleChange = (event) => {
        setType(event.target.value);
    }

    return (
        <div>
            <img src={Expensetracker} className='float-right' alt='img' />
            <div className='w-1/2 h-full float-left text-center' style={{ marginTop: '3rem' }}>
                <h1 className='text-indigo-900	text-6xl'>Expense Manager</h1>
                <div className='text-left'>
                    <h4>Your Balance is</h4>
                    <h2>{Balance}</h2>
                </div>
                <div className='container w-2/3  shadow-cardShadow' style={{  margin:'auto',marginTop: '4rem' }}>
                    <div className='grid' style={{padding:"2rem"}}>
                    <TextField
                        sx={{ m: 1, minWidth: 240 }}
                            label="Total Amount"
                            id="standard-size-normal"
                            defaultValue={TotalAmount}
                            variant="standard"
                            className='row text-left'
                        />

                        <FormControl variant="standard" sx={{ m: 1, minWidth: 240 }} className='row text-left'>
                            <InputLabel>Transaction Type</InputLabel>
                            <Select
                                id="Transaction_type"
                                value={Type}
                                onChange={handleChange}
                                label="Transaction Type" 
                            >
                                <MenuItem value='+'>Credit</MenuItem>
                                <MenuItem value='-'>Debit</MenuItem>
                            </Select>
                        </FormControl>

                        <TextField
                        sx={{ m: 1, minWidth: 240 }}
                            label="Amount Credited/Debited"
                            id="standard-size-normal"
                            variant="standard"
                            className='row text-left'
                        />
                    </div>
                </div>
            </div>


        </div>
    )
}

export default ExpenseTracker
