import axios from 'axios'
import React from 'react'

const ExpenseHistory = ({ type, note,Amount, setOption,setBalance, Balance}) => {
    if(localStorage.token) {
        var headers = localStorage.token
    }
    const expenses = axios.get("http://localhost:5000/expensetracker",
    {headers:{
        token:headers
       }}
    )

    console.log(expenses.res)
    const onClick=(e)=> {
        e.preventDefault()
        setOption('Transaction')
        type==='+'?
        setBalance(Number(Balance) + Number(Amount)) :
        setBalance(Number(Balance) - Number(Amount));
    }

    return (
        <div>
            <div className='w-5/6 shadow-listShadow' style={{padding:'5px', margin:'auto', marginTop:'1rem', height:'2rem'}}>
                <span className='text-left float-left' style={{marginLeft:'10px'}}>{note}</span>
                <span className='float-right' style={{marginRight:'10px'}}>
                    {
                        type==='+'?
                        <p className='text-green-400'>{type} {Amount}</p> :
                        <p className='text-red-400'>{type} {Amount}</p>

                    } 
                </span>
            </div>
            <div className='row'>
                    <button type='button' onClick={(e)=>onClick(e)} className='sm:w-1/3 bg-purple text-white col' style={{marginTop:"0.8rem", marginRight:"5px", padding:'5px'}}>Add 
                    {Option==='Transaction'?
                    ' Transaction' :
                    ' Transactions'
                    }
                    </button>
                    <button type='button' onClick={()=>setOption('History')} className='sm:w-1/3 bg-purple text-white col' style={{marginTop:"0.8rem",padding:'5px'}}>
                    {Option==='Transaction'?
                    'View ' :
                    'Delete '
                    }
                         History</button>
                    </div>
                        
       </div>
    )
}

export default ExpenseHistory
