import React, { useState } from 'react'
import InputFields from '../components/InputFields'
import Expensetracker from '../assets/expenseTracker.PNG'
import TextField from '@mui/material/TextField'
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import ExpenseTrackerForm from '../components/ExpenseTrackerForm';
import  ExpenseHistory  from '../components/ExpenseHistory';

const ExpenseTracker = () => {
    const [Type, setType] = useState('')
    const [Balance, setBalance] = useState()
    const [Note, setNote] = useState('')
    const [Option, setOption] = useState('Transaction')
    const handleChange = (event) => {
        setType(event.target.value);
    }


    return (
        <div>
            <img src={Expensetracker} className='float-right' alt='img' />
            <div className='w-1/2 h-full float-left text-center' style={{ marginTop: '3rem' }}>
                <h1 className='text-indigo-900	text-6xl'>Expense Manager</h1>
                <div style={{margin:'auto',marginTop:"1rem"}}>
                    <h2 className=''>Your Balance is</h2>
                    <h1 className='font-bold text-xl'>â‚¹ {Balance}</h1>
                </div>
                <div className='container w-2/3  shadow-cardShadow' style={{  margin:'auto',marginTop: '1.7rem' }}>
                    <div className='grid' style={{padding:"2rem"}}>
                    {Option==='Transaction'?
                    <ExpenseTrackerForm /> :
                    <ExpenseHistory />
                    }
               
                    <div className='row'>
                    <button type='submit' onClick={()=>setOption('Transaction')} className='w-1/3 bg-purple text-white col' style={{marginTop:"0.8rem", marginRight:"5px", padding:'5px'}}>Add Transaction</button>
                    <button type='submit' onClick={()=>setOption('History')} className='w-1/3 bg-purple text-white col' style={{marginTop:"0.8rem",padding:'5px'}}>
                    {Option==='Transaction'?
                    'View ' :
                    'Delete '
                    }
                         History</button>
                    </div>
                        
                    </div>
                </div>
            </div>


        </div>
    )
}

export default ExpenseTracker
